import { Component, NgZone } from '@angular/core';
import { Validators, FormBuilder, FormGroup, FormControl } from '@angular/forms';
import { Platform, ActionSheetController, NavController, NavParams, LoadingController, ToastController, ModalController } from 'ionic-angular';
import { OrdersService } from '../orders/orders.service';
import moment from 'moment';
import { CustomersService } from '../clientes/customers.service';
import { MapsPage } from '../maps/maps';
import { Camera, CameraOptions } from '@ionic-native/camera';
import { RoutesPage } from '../routes/routes';


@Component({
  selector: 'add-customer-page',
  templateUrl: 'add-customer.html'
})

export class AddCustomerPage {

  validations_form: FormGroup;
  loading: any;
  customer: any;
  routesPage = 2;
  defaultImage: any = "";
  customerImage: any = "";
  cameraOptions: CameraOptions = {
    quality: 50,
    targetWidth: 500,
    targetHeight: 500,
    allowEdit: true,
    destinationType: this.camera.DestinationType.DATA_URL,
    encodingType: this.camera.EncodingType.PNG,
    mediaType: this.camera.MediaType.PICTURE
  };

  dias_semana = [
    { "value":"1", "label":"Lunes" },
    { "value":"2", "label":"Martes" },
    { "value":"3", "label":"Miercoles" },
    { "value":"4", "label":"Jueves" },
    { "value":"5", "label":"Viernes" },
    { "value":"6", "label":"Sabado" },
    { "value":"7", "label":"Domingo" }
  ];
  // dias_semana.selectedValues= [];
  myFriendsArray = [{$value:1,fullName:'someName', userName:'username1'},{$value:2,fullName:'someName2', userName:'username2'},{$value:3,fullName:'someName3', userName:'username3'}]
  selectedFriendsArray = [];

  galleryOptions: CameraOptions = {
    quality: 50,
    targetWidth: 500,
    targetHeight: 500,
    allowEdit: true,
    destinationType: this.camera.DestinationType.DATA_URL,
    encodingType: this.camera.EncodingType.PNG,
    mediaType: this.camera.MediaType.PICTURE,
    sourceType: this.camera.PictureSourceType.PHOTOLIBRARY
  }

  constructor(
    public navCtrl: NavController,
    public navParams: NavParams,
    public formBuilder: FormBuilder,
    public customersService: CustomersService,
    public loadingCtrl: LoadingController,
    public modalCtrl: ModalController,
    public toastCtrl: ToastController,
    public ordersService: OrdersService,
    public actionSheetCtrl: ActionSheetController,
    public platform: Platform,
    public camera: Camera,

  ) {
    this.loading = this.loadingCtrl.create();
    this.customer = this.navParams.get('customer');
    this.routesPage = this.navParams.get('routesPage');

    console.log(this.customer);
  }

  showMap() {
    // reset
    let lat = '';
    let lng = '';
    if (this.customer) {
      lng = this.customer.rm_longitude;
      lat = this.customer.rm_latitude;
    }
    let modal = this.modalCtrl.create(MapsPage, { 'lat': lat, 'lng': lng, 'markerDrag': true });
    modal.onDidDismiss(data => {
      if (data) {
        this.validations_form.get('rm_longitude').setValue(data.lng);
        this.validations_form.get('rm_latitude').setValue(data.lat);
      }
    });
    modal.present();
  }

  takePicture() {
    let me = this;
    let actionSheet = this.actionSheetCtrl.create({
      title: 'Select Picture from',
      buttons: [
        {
          text: 'Camera',
          icon: !this.platform.is('ios') ? 'camera' : null,
          handler: () => {
            me.camera.getPicture(me.cameraOptions).then((imageData) => {
              // imageData is a base64 encoded string
              me.customerImage = imageData;
            }, (err) => {
              console.log(err);
            });
          }
        }, {
          text: 'Gallery',
          icon: !this.platform.is('ios') ? 'albums' : null,
          handler: () => {
            me.camera.getPicture(me.galleryOptions).then((imageData) => {
              // imageData is a base64 encoded string
              me.customerImage = imageData;
            }, (err) => {
              console.log(err);
            });
          }
        }, {
          text: 'Cancel',
          role: 'cancel',
          icon: !this.platform.is('ios') ? 'close' : null,
          handler: () => {
            console.log('Cancel clicked');
          }
        }
      ]
    });
    actionSheet.present();
  }

  ionViewDidEnter() {
    if (this.customer) {
      this.validations_form.get('name').setValue(this.customer.name);
      this.validations_form.get('street').setValue(this.customer.street);
      this.validations_form.get('phone').setValue(this.customer.phone);
      this.validations_form.get('mobile').setValue(this.customer.mobile);
      this.validations_form.get('email').setValue(this.customer.email);
      this.validations_form.get('rm_dias_semana').setValue(this.customer.rm_dias_semana);
      this.validations_form.get('nit').setValue(this.customer.nit);
      this.validations_form.get('razon_social').setValue(this.customer.razon_social);
      this.validations_form.get('rm_longitude').setValue(this.customer.rm_longitude);
      this.validations_form.get('rm_latitude').setValue(this.customer.rm_latitude);
      this.validations_form.get('property_product_pricelist').setValue(this.customer.property_product_pricelist);
      this.customerImage = this.customer.photo_m;
    }
  }

  ionViewWillLoad() {

    this.validations_form = this.formBuilder.group({
      name: new FormControl('', Validators.required),
      street: new FormControl('', []),
      phone: new FormControl('', []),
      mobile: new FormControl('', []),
      rm_dias_semana: new FormControl('', Validators.required),
      email: new FormControl('', [Validators.pattern(this.customersService.EMAIL_REGEX)]),
      nit: new FormControl('', []),
      razon_social: new FormControl('', []),
      rm_longitude: new FormControl('', []),
      rm_latitude: new FormControl('', []),
      // property_product_pricelist: new FormControl({ value: 1, disabled: false }, [Validators.required]),
      property_product_pricelist: new FormControl('', [Validators.required]),
    });
  }

  validation_messages = {
    'name': [
      { type: 'required', message: 'Se requiere el nombre.' }
    ],
    'street': [
      { type: 'required', message: 'Se requiere el direccin.' }
    ],
    'phone': [
      { type: 'required', message: 'Se requiere un telfono.' }
    ],
    'rm_dias_semana': [
      { type: 'required', message: 'Se requiere al menos un dia de visita a la semana.' }
    ],
    'property_product_pricelist': [
      { type: 'required', message: 'Se requiere un property_product_pricelist.' }
    ],
    'mobile': [
      { type: 'required', message: 'Se requiere un celular.' }
    ],
    'email': [
      { type: 'required', message: 'Se requiere un correo electronico.' },
      { type: 'pattern', message: 'Debe ser un correo electrnico vlido.' },
    ],
    'rm_latitude': [
      { type: 'required', message: 'Se requiere un latitud.' }
    ],
    'rm_longitude': [
      { type: 'required', message: 'Se requiere un longitud.' }
    ],
  };

  onSubmit(values) {
    console.log(values);
    values.photo_m = this.customerImage ? this.customerImage : "";
    values.rm_sync_date_time = moment(new Date()).format("YYYY-MM-DD HH:mm:ss");

    if (this.customer) {
      values.id = this.customer.id;
      values._rev = this.customer._rev;
      values.user_id = this.customer.user_id;
      // values.rm_dias_semana = this.customer.rm_dias_semana;

      // values.newCustomer = this.customer.newCustomer;
      values.newCustomer = 2;
      // values.property_product_pricelist = this.customer.property_product_pricelist;
      console.log("updateCustomer:" + JSON.stringify(values));
      this.customersService.updateCustomer(values);
    } else {
      values.id = 'NEW' + Date.now();
      values.user_id = this.ordersService.loginId;
      values.newCustomer = 1;
      // values.property_product_pricelist = this.customer.property_product_pricelist;
      // values.property_product_pricelist = 1;
      console.log("addCustomer:" + JSON.stringify(values));
      this.customersService.addCustomer(values);
    }

    let toast = this.toastCtrl.create({
      message: "Cliente almacenado con xito!",
      duration: 3000,
      cssClass: 'toast-success',
      position: 'bottom',
    });
    toast.present();
    if (this.routesPage && this.routesPage == 1) {
      this.navCtrl.setRoot(RoutesPage);
    } else {
      this.navCtrl.pop();
    }
  }
}
